import { Menu } from '@headlessui/react'
import { Icon } from 'Icons'


function Auth() {

   const user = {
       name: 'Muhammed Bahadir',
       avatar: 'https://i.scdn.co/image/ab6775700000ee856fca122911ed9eec4ce60c1e' 
      }

    return (
      <Menu as="nav" className={"relative"} >
        {({open : boolean }) => (
          <>
             <Menu.Button className={'flex items-center h-8 rounded-3xl pr-2  hover:bg-active' }>
            <img src={user.avatar} className={"w-8 h-8 rounded-full p-0.5 mr-2"} />
            <span className="text-sm font-semibold mr-2">{user.name}</span> 
            <span className={open == true && 'rotate-180'}>     
            <Icon size={16} name="downDir" />
            </span>    
        </Menu.Button>
        <Menu.Items className={"absolute top-full right-0 w-w-48 bg-active rounded-md"}>
          <Menu.Item> 
            {({ active }) => (
              <a
                className={`${active && 'bg-blue-500'}`}
                href="/account-settings"
              >
                Account settings
              </a>
            )}
          </Menu.Item>
        </Menu.Items>
          </>
        )}
      </Menu>

    )
}

export default Auth
